import React, { useState } from "react";

export default function Chatbot() {
  const [messages, setMessages] = useState([
    { type: 'bot', text: 'Hola üêæ, soy tu asistente virtual.<br>¬øEn qu√© puedo ayudarte hoy?' }
  ]);
  const [showOptions, setShowOptions] = useState(true);

  const respuestas = {
    'comida-recomendada': 'Los perros pueden comer croquetas balanceadas o comida natural supervisada por un veterinario. Evita grasas y condimentos.',
    'frecuencia': 'Cachorros: 3-4 veces al d√≠a. Adultos: 2 veces. Mayores: 1-2 veces seg√∫n actividad.',
    'alimentos-prohibidos': 'Evita chocolate, cebolla, uvas, aguacate y huesos cocidos, pueden ser t√≥xicos.',
    'vacunas': 'Vacunas esenciales: Moquillo, Parvovirus, Hepatitis, Rabia y Leptospirosis.',
    'desparasitacion': 'Cada 3 meses es recomendable desparasitar con productos aprobados por tu veterinario.',
    'emergencias': 'Mant√©n calma. Si hay envenenamiento, heridas graves o convulsiones, acude al veterinario inmediatamente.',
    'entrenamiento': 'Refuerza conductas positivas con premios y paciencia. Evita castigos.',
    'ansiedad': 'Crea rutinas, paseos y juguetes para reducir el estr√©s. Evita dejarlo solo por largos periodos.',
    'agresividad': 'Consulta con un et√≥logo. La agresividad puede ser por miedo o territorialidad.',
    'contacto-veterinario': 'Para contactar a un veterinario, llama al +52 55 1234 5678 o visita nuestra p√°gina de contacto.',
    'contacto-etologo': 'Para contactar a un et√≥logo, env√≠a un correo a etologo@petconnect.com o llama al +52 55 9876 5432.',
    'contacto-general': 'Para informaci√≥n general, visita nuestra p√°gina de contacto o llama al +52 55 1111 2222.'
  };

  const showSubOptions = (topic) => {
    let subOptions = [];
    let botMsg = '';

    if (topic === 'alimentacion') {
      botMsg = 'üêæ Alimentaci√≥n - ¬øQu√© deseas saber?';
      subOptions = [
        { text: 'Tipos de comida recomendada', action: 'comida-recomendada' },
        { text: 'Frecuencia de alimentaci√≥n', action: 'frecuencia' },
        { text: 'Alimentos prohibidos', action: 'alimentos-prohibidos' }
      ];
    } else if (topic === 'salud') {
      botMsg = 'üíä Salud - ¬øQu√© te gustar√≠a consultar?';
      subOptions = [
        { text: 'Vacunas necesarias', action: 'vacunas' },
        { text: 'Desparasitaci√≥n', action: 'desparasitacion' },
        { text: 'Qu√© hacer en emergencias', action: 'emergencias' }
      ];
    } else if (topic === 'comportamiento') {
      botMsg = 'üê∂ Comportamiento - Elige un tema:';
      subOptions = [
        { text: 'Entrenamiento b√°sico', action: 'entrenamiento' },
        { text: 'Ansiedad o estr√©s', action: 'ansiedad' },
        { text: 'Agresividad o miedos', action: 'agresividad' }
      ];
    } else if (topic === 'especialista') {
      botMsg = 'üë®‚Äç‚öïÔ∏è Contactar a un especialista - ¬øC√≥mo podemos ayudarte?';
      subOptions = [
        { text: 'Contactar veterinario', action: 'contacto-veterinario' },
        { text: 'Contactar et√≥logo', action: 'contacto-etologo' },
        { text: 'Informaci√≥n general', action: 'contacto-general' }
      ];
    }

    setMessages(prev => [...prev, { type: 'bot', text: botMsg }]);
    setShowOptions(false);
    setTimeout(() => {
      setMessages(prev => [...prev, { type: 'options', options: subOptions }]);
    }, 500);
  };

  const showAnswer = (answer, text) => {
    const reply = respuestas[answer] || 'Lo siento, no tengo informaci√≥n sobre eso üêæ.';
    setMessages(prev => [
      ...prev,
      { type: 'user', text: `üëâ ${text}` },
      { type: 'bot', text: reply }
    ]);
  };

  return (
    <main>
      {/* Hero Section with Chatbot */}
      <div className="hero">
        <h2>¬øTienes dudas sobre tu mascota?</h2>
        <p>Nuestro Chat-Bot te brinda asesor√≠a al instante o te conecta con un especialista.</p>
        <div className="chatbot-seccion">
          <div className="chatbot-box">
            <div className="chat-header">
              <h3>üêï‚Äçü¶∫ PetBot ü§ñ</h3>
            </div>

            <div className="chat-body">
              {messages.map((msg, index) => (
                <div key={index}>
                  {msg.type === 'bot' && <div className="bot-msg" dangerouslySetInnerHTML={{ __html: msg.text }}></div>}
                  {msg.type === 'user' && <div className="user-msg">{msg.text}</div>}
                  {msg.type === 'options' && (
                    <div className="options">
                      {msg.options.map((option, i) => (
                        <button key={i} onClick={() => showAnswer(option.action, option.text)}>
                          {option.text}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              ))}
              {showOptions && (
                <div className="options" id="main-options">
                  <button onClick={() => showSubOptions('alimentacion')}>üçñ Alimentaci√≥n</button>
                  <button onClick={() => showSubOptions('salud')}>üíä Salud</button>
                  <button onClick={() => showSubOptions('comportamiento')}>üê∂ Comportamiento</button>
                  <button onClick={() => showSubOptions('especialista')}>üë®‚Äç‚öïÔ∏è Contactar a un especialista</button>
                </div>
              )}
            </div>

            <div className="chat-footer">
              <small>PetConnect Asistente Virtual</small>
            </div>
          </div>
        </div>
      </div>
    </main>
  );
}
